alter trigger M_T_AUDR_G3E_STYLERULE_RULE disable;
alter trigger M_T_AUD_G3E_STYLERULE_RULE disable;

declare
  TYPE numArray IS TABLE OF NUMBER INDEX BY PLS_INTEGER;
  arrSNO  numArray;
  i       PLS_INTEGER;
begin
  select distinct G3E_SNO
    bulk collect into arrSNO
    from G3E_STYLERULE
    where G3E_RULE in ('Secondary Conductor Linear', 'Secondary Conductor Linear - OMS'
                      ,'Secondary Conductor - OH Linear','Secondary Conductor - OH Detail Linear  '
                      ,'Secondary Conductor - UG Linear','Secondary Conductor - OH Network Linear'
                      ,'Secondary Conductor - UG Network Linear');
  
  delete from G3E_STYLERULE where G3E_RULE = 'Secondary Conductor Linear';
  delete from G3E_STYLERULE where G3E_RULE = 'Secondary Conductor Linear - OMS';
  
  delete from G3E_STYLERULE where G3E_RULE = 'Secondary Conductor - OH Linear'; -- obsolete
  delete from G3E_STYLERULE where G3E_RULE = 'Secondary Conductor - OH Detail Linear  '; -- obsolete
  delete from G3E_STYLERULE where G3E_RULE = 'Secondary Conductor - UG Linear'; -- obsolete
  delete from G3E_STYLERULE where G3E_RULE = 'Secondary Conductor - OH Network Linear'; -- obsolete
  delete from G3E_STYLERULE where G3E_RULE = 'Secondary Conductor - UG Network Linear'; -- obsolete

  for i in 1..arrSNO.COUNT loop
    delete from G3E_LINESTYLE where G3E_SNO = arrSNO(i);
    delete from G3E_STYLE where G3E_SNO = arrSNO(i);
  end loop;
end;
/

alter trigger M_T_AUDR_G3E_STYLERULE_RULE enable;
alter trigger M_T_AUD_G3E_STYLERULE_RULE enable;


delete from G3E_LINESTYLE where G3E_SNO between 53301 and 53317;
delete from G3E_NORMALIZEDSTROKE where G3E_SPNO between 53101 and 53199;
