UPDATE G3E_ATTRIBUTE SET G3E_FINO =1, G3E_FUNCTIONALORDINAL = 1,
G3E_FUNCTIONALTYPE ='SetValue' ,G3E_INTERFACEARGUMENT = ''
WHERE G3E_CNO = (SELECT G3E_CNO FROM G3E_COMPONENT WHERE G3E_USERNAME='Conduit Linear') AND G3E_FIELD = 'G3E_GEOMETRY';

UPDATE G3E_ATTRIBUTE SET G3E_FINO = (SELECT G3E_FINO FROM G3E_FUNCTIONALINTERFACE WHERE G3E_USERNAME ='Copy Actual Length Attribute'), G3E_FUNCTIONALORDINAL = 1,
G3E_FUNCTIONALTYPE ='SetValue', G3E_INTERFACEARGUMENT = ''
WHERE G3E_CNO = 1 AND G3E_FIELD = 'LENGTH_GRAPHIC_Q';

UPDATE G3E_ATTRIBUTE SET G3E_FINO = (SELECT G3E_FINO FROM G3E_FUNCTIONALINTERFACE WHERE G3E_USERNAME ='Copy Actual Length Attribute'), G3E_FUNCTIONALORDINAL = 2,
G3E_FUNCTIONALTYPE ='Update', G3E_INTERFACEARGUMENT = ''
WHERE G3E_CNO = 1 AND G3E_FIELD = 'LENGTH_ACTUAL_Q';


